import React, { useState, useEffect } from 'react';
import './App.css';
import Cover from './component/Cover';
import Quiz from './component/Quiz';
import { nanoid } from 'nanoid';
import { decode } from 'html-entities';



// url : https://opentdb.com/api.php?amount=10&category=9

function App() {
  const [quizData, setQuizData] = useState({})
  const [startQuiz, setStartQuiz] = useState(false)
  const [showScore, setShowScore] = useState(false)

  const url = "https://opentdb.com/api.php?amount=10"

  useEffect(()=>{
    fetch(url)
      .then(response => response.json())
      .then(data => setQuizData(data.results))
  },[])
  console.log(quizData)

  // store quizData into quizDataList 
  const quizDataList = []
  quizDataList = quizData.map(quiz => {
      quizDataList.push(
    {
      id: nanoid(),
      question: decode(quiz.quistion),
      answer: [quiz.correct_answer, quiz.incorrect_answers.map(ans => ans)],
      correct_answer: quiz.correct_answer
    })
  })


  function start(){
    setStartQuiz(true)
  }


  return (
    <main>
      <div className="container">
        <div className="bg-yellow-block"></div>
          {startQuiz ? <Quiz quizData={quizData} showScore={showScore}/> : <Cover start={start}/>}
        <div className="bg-blue-block"></div>
      </div>
    </main>
  );
}

export default App;
