import React from 'react';
import { decode } from 'html-entities';


export default function Quiz(props){
    const quizes = props.quizData.map(item => {
        return(
            <div className='quiz-container'>
                <h2 className='quiz-question'>{decode(item.question)}</h2>
                <ul className='quiz-ans-container'>
                    {decode(item.incorrect_answers).map(ans => <li className='quiz-ans'>{ans}</li>)}
                    <li className='quiz-ans'>{decode(item.correct_answer)}</li>
                </ul>
                <hr />
            </div>
        )
    })
    let quizDataList = []
    quizDataList = props.quizData.map(quiz => {
      quizDataList.push(
    {
      id: nanoid(),
      question: decode(quiz.quistion),
      answer: [quiz.correct_answer, quiz.incorrect_answers.map(ans => ans)],
      correct_answer: quiz.correct_answer
    })
  })

    function checkAnsButton(){
        return(
            <div className='quiz-score-container'>
                <button className='btn'>Check Answer</button>
            </div>
        )
    } 

    function playAgainButton(){
        return(
            <div className='quiz-score-container'>
                <h3>You scored /10 correct answer</h3>
                <button className="btn">Play again</button>
            </div>
        )
    }
    return(
        <div className='quizes-container'>
            {quizes}
            {props.showScore ? 
            <div className='quiz-score-container'>
                <h3>You scored /10 correct answer</h3>
                <button className="btn">Play again</button>
            </div> : 
            <div className='quiz-score-container'>
                <button className='btn'>Check Answer</button>
            </div>
            }
        </div>
    )
}
