import React from 'react';
import { decode } from 'html-entities';
import { nanoid } from 'nanoid';



export default function Quiz(props){
    console.log(props.id)
    function handleClickCheckAnswer(){
        props.quizData.map(item=>{
            item.props.checkScore(item.id)
        })
    }

    const quizes = props.quizData.map(item => {
        return(
            <div className='quiz-container'>
                <h2 className='quiz-question'>{item.question}</h2>
                <div className='quiz-ans-container'>

                    {/* {item.answers.map(ans => <button onClick={props.handleClick(item.answer)} className='quiz-ans'>{ans}</button>)} */}
                    {item.answers.map(ans => <button onClick={()=>props.handleClickAnswer(item.id, ans)} className={item.selected === ans ? "quiz-ans selected" : "quiz-ans"} >{ans}</button>)}

                </div>
                <hr />
            </div>
        )
    })

    return(
        <div className='quizes-container'>
            {quizes}

            {props.showScore ? 
            <div className='quiz-score-container'>
                <h3>You scored {props.score}/10 correct answer</h3>
                <button className="btn" onClick={props.playAgain}>Play again</button>
            </div> : 
            <div className='quiz-score-container'>
                <button className='btn' onClick={handleClickCheckAnswer}>Check Answer</button>
            </div>
            }
        </div>
    )
}
