import React from 'react';
import { decode } from 'html-entities';
import { nanoid } from 'nanoid';



export default function Quiz(props){
    console.log(props.quizDataList)
    const quizes = props.quizDataList.map(item => {
        return(
            <div className='quiz-container' key={item.id}>
                <h2 className='quiz-question'>{item.question}</h2>
                <div className='quiz-ans-container'>

                    {item.answer.map(ans => <li className='quiz-ans'>{ans}</li>)}

                </div>
                <hr />
            </div>
        )
    })

    return(
        <div className='quizes-container'>
            {quizes}
            {props.showScore ? 
            <div className='quiz-score-container'>
                <h3>You scored /10 correct answer</h3>
                <button className="btn">Play again</button>
            </div> : 
            <div className='quiz-score-container'>
                <button className='btn'>Check Answer</button>
            </div>
            }
        </div>
    )
}
