import React, { useState, useEffect } from 'react';
import './App.css';
import Cover from './component/Cover';
import Quiz from './component/Quiz';
import { nanoid } from 'nanoid';
import { decode } from 'html-entities';



// url : https://opentdb.com/api.php?amount=10&category=9

function App() {
  const [quizData, setQuizData] = useState({})
  const [startQuiz, setStartQuiz] = useState(false)
  const [showScore, setShowScore] = useState(false)

  const url = "https://opentdb.com/api.php?amount=10"

  useEffect(()=>{
    async function getData(){
      const res = await fetch(url)
      const data = await res.json()
      setQuizData(data.results)
    }
    getData()
    function x (){
      for(let i = 0; i < quizData.length; i++){
        let quiz = {
          id: nanoid(),
          question: quizData[i].question,
          answer: [quizData[i].correct_answer, quizData[i].incorrect_answer.map(ans => ans)],
          correct_answer: quizData[i].correct_answer
        }
        quizDataList.push(quiz)
      }
    }
    x()
  },[])
  console.log(quizData)
  let quizDataList = []
  function x (){
    for(let i = 0; i < quizData.length; i++){
      console.log(quizData[i])
    }
  }
  x()


  function start(){
    setStartQuiz(true)
  }


  return (
    <main>
      <div className="container">
        <div className="bg-yellow-block"></div>
          {startQuiz ? <Quiz quizData={quizData} showScore={showScore}/> : <Cover start={start}/>}
        <div className="bg-blue-block"></div>
      </div>
    </main>
  );
}

export default App;
