import React, { useState, useEffect } from 'react';
import './App.css';
import Cover from './component/Cover';
import Quiz from './component/Quiz';
import { nanoid } from 'nanoid';
import { decode } from 'html-entities';



// url : https://opentdb.com/api.php?amount=10&category=9

function App() {
  const [quizData, setQuizData] = useState({})
  const [quizDataList, setQuizDataList] = useState([])
  const [startQuiz, setStartQuiz] = useState(false)
  const [showScore, setShowScore] = useState(false)
  const [click, setClick] = useState(false)

  const url = "https://opentdb.com/api.php?amount=10"
  const shuffleArray = (arr) => arr.sort(()=> Math.random() - 0.5)


  useEffect(()=>{
    async function getData(){
      const res = await fetch(url)
      const data = await res.json()
      let quizList = []
      data.results.forEach(question => {
        quizList.push({id:nanoid(), question: question.question, correct: question.correct_answer, selected: null, checked: false, answers: shuffleArray(...question.correct_answer, question.incorrect_answers)})
      })
      setQuizData(quizList)
    }
    getData()
  },[])

  function dataList (){
    for(let i = 0; i < quizData.length; i++){
      let quiz = {
        ...quizData[i],
        key: nanoid(),
        question: decode(quizData[i].question),
        answer: [quizData[i].correct_answer, ...quizData[i].incorrect_answers].sort(),
        correct_answer: quizData[i].correct_answer
      }
      quizDataList.push(quiz)  
    }
    console.log(quizDataList)
  }

  function start(){
    setStartQuiz(true)
    dataList()
  }

  function clickAnswer(){
    setClick(old => !old)
  }

  return ( quizData.length > 0 ? (
    <main>
      <div className="container">
        <div className="bg-yellow-block"></div>
          {startQuiz ? <Quiz quizDataList={quizDataList} showScore={showScore} click={click} clickAnswer={clickAnswer}/> : <Cover start={start}/>}
        <div className="bg-blue-block"></div>
      </div>
    </main>
  ) : <div className='load'>Loading...</div>

  )
}

export default App;
