import React from 'react';
import { decode } from 'html-entities';
import { nanoid } from 'nanoid';



export default function Quiz(props){
    const answers = props.answers

    // function findClickedAnswer() {
    //     return answers.find(ans => {
    //         return ans === currentNoteId
    //     }) 
    // }

    function handleClick(answer){
        if(props.quizData.checked){
            return
        }
        props.handleClickAnswer(props.id, answer)
    }

    function test(ans){
        console.log(ans)
    }
    const quizes = props.quizData.map(item => {
        return(
            <div className='quiz-container'>
                <h2 className='quiz-question'>{item.question}</h2>
                <div className='quiz-ans-container'>

                    {/* {item.answers.map(ans => <button onClick={props.handleClick(item.answer)} className='quiz-ans'>{ans}</button>)} */}
                    {item.answers.map(ans => <button onClick={()=>test(item.answera)} className='quiz-ans'>{ans}</button>)}

                </div>
                <hr />
            </div>
        )
    })

    return(
        <div className='quizes-container'>
            {quizes}

            {props.showScore ? 
            <div className='quiz-score-container'>
                <h3>You scored /10 correct answer</h3>
                <button className="btn">Play again</button>
            </div> : 
            <div className='quiz-score-container'>
                <button className='btn'>Check Answer</button>
            </div>
            }
        </div>
    )
}
