import React, { useState, useEffect } from 'react';
import './App.css';
import Cover from './component/Cover';
import Quiz from './component/Quiz';

// url : https://opentdb.com/api.php?amount=10&category=9

function App() {

  const [startQuiz, setStartQuiz] = useState(false)

  const url = "https://opentdb.com/api.php?amount=10&category=9"

  useEffect(()=>{
    fetch(url)
    .then(res => res.json())
    .then(data => console.log(data.result))
  },[])




  function start(){
    setStartQuiz(true)
  }
  return (
    <main>
      <div className="container">
        <div className="bg-yellow-block"></div>
          {startQuiz ? <Quiz /> : <Cover start={start}/>}
        <div className="bg-blue-block"></div>
      </div>
    </main>
  );
}

export default App;
